<?php

/**
 * OhrmLeaveEntitlementTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class OhrmLeaveEntitlementTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object OhrmLeaveEntitlementTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('OhrmLeaveEntitlement');
    }
    
    
    public static function getEmployeeLeave($empno,$year=""){
        
        if(!isset($year)){
        $q = Doctrine_Query::create()
                            ->from('OhrmLeaveEntitlement')
                         ->where(" `emp_number`=$empno");
                    $q->execute();
              return $q->fetchArray(Doctrine::HYDRATE_SCALAR);
        }
        else{
       
        $q = Doctrine_Query::create()
                            ->from('OhrmLeaveEntitlement')
                         ->where(" `emp_number`=$empno")
                ->andWhere("DATE_FORMAT(`from_date`,'%Y') = '$year'")
                ->andWhere("DATE_FORMAT(`to_date`,'%Y') = '$year'");
                       $q->execute(); 
               return $q->fetchArray(Doctrine::HYDRATE_SCALAR); 
            
        }
  
    }
    
   public static function getEmployeeAnnualLeave($empno,$year=""){
        
        if(!isset($year)){
        $q = Doctrine_Query::create()
                            ->from('OhrmLeaveEntitlement')
                         ->where(" `emp_number`=$empno");
                    $q->execute();
              return $q->fetchArray(Doctrine::HYDRATE_SCALAR);
        }
        else{
       
        $q = Doctrine_Query::create()
                            ->from('OhrmLeaveEntitlement')
                         ->where(" `emp_number`=$empno")
                ->andWhere(" `leave_type_id`=2")
                ->andWhere("DATE_FORMAT(`from_date`,'%Y') = '$year'")
                ->andWhere("DATE_FORMAT(`to_date`,'%Y') = '$year'");
                       $q->execute(); 
               return $q->fetchArray(Doctrine::HYDRATE_SCALAR); 
            
        }
  
    }
    
    
    
}