<?php

/**
 * OhrmLocationTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class OhrmLocationTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object OhrmLocationTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('OhrmLocation');
    }
    
    public static function getAllLocations(){
        $locations=self::getInstance()->findAll();
        return $locations;
    }
    public static function getOrderedLocations(){
            return Doctrine_Query::create()->from('OhrmLocation')->orderBy("name ASC")->execute();
    }
    
     public static function getLocation($id){
        $location=self::getInstance()->find($id);
        return $location;
    }
    
        /***************hydrate locations**************/
            public static function hydrateLocations() {
        try {
$locations= self::getInstance()->findAll();
$choices["all"]="All Locations";
foreach ($locations as $location) {
    
    $choices[$location->getId()]=$location->getName();
}
return $choices;
        } catch (Exception $e) {
            throw new DaoException($e->getMessage());
        }
    }
   
}