<?php

/**
 * OhrmUserRoleScreenTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class OhrmUserRoleScreenTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object OhrmUserRoleScreenTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('OhrmUserRoleScreen');
    }
    
    public static function  getUserRoleScreens($roleid){
             $q = Doctrine_Query::create()
                            ->from('OhrmUserRoleScreen')
                         ->where(" `user_role_id`=$roleid");
 
            $q->execute();
        
      return $q->fetchArray();
    }
    
    public static function  getUserRoleScreen($id){
             $q = Doctrine_Query::create()
                            ->from('OhrmUserRoleScreen')
                         ->where(" `id`=$id");
 
            $q->execute();
        
      return $q->fetchOne();
    }
    
     public static function  UpdateUserRoleScreen($id,$permission,$status){
      $q=Doctrine_Query::create()
     ->update('OhrmUserRoleScreen q')
     ->set('q.'.$permission,$status)
    
     ->where('q.id=?',$id)
     ->execute();
    }
}